using System.Collections; using System.Collections.Generic; using UnityEngine.UI; using UnityEngine.SceneManagement;  using UnityEngine;  public class ProgressBar : MonoBehaviour {  	public string Scene; 	bool x; 	float ftime = .01f;      	public void Next () { // arttır 		x = true;  	}  	public void Prev() { // azaltır 		x = false; 	}    	void Start () { 		x = false;   	}  	void Update() { 		Image image = GetComponent<Image> (); 		if (x == true) { 			image.fillAmount += ftime;   			if(image.fillAmount == 1)  				SceneManager.LoadScene (Scene); 		} 		else 			image.fillAmount -= ftime; 	} 	public void Menu() { 		SceneManager.LoadScene (Scene); 	}  } 